import os

import matplotlib.pyplot as plt
import numpy as np

from pmesh.test.base import AbstractPmeshTest


class Test(AbstractPmeshTest):

    def test_from_file(self):
        path_output = os.path.expanduser('~/data/pmesh/nfie_out')
        path_esmf_format_nc = os.path.join(path_output, 'catchments_esmf_format_1d_20160302-1643.nc')
        element_idx = 253
        with self.nc_scope(path_esmf_format_nc) as ds:
            element_count = ds.variables['numElementConn'][element_idx]
            counts = ds.variables['numElementConn'][0:element_idx]
            start_idx = sum(counts)
            elementConn = ds.variables['elementConn']
            node_indices = elementConn[start_idx:start_idx + element_count]
            nodeCoords = ds.variables['nodeCoords']
            coords = nodeCoords[node_indices, :]
            self.plot_element(coords)

    def plot_element(self, arr):
        fig = plt.figure(figsize=(8, 6))
        ax = fig.add_subplot(111)
        plt.plot(arr[:, 0], arr[:, 1])
        # plt.plot(arr[:, 0], arr[:, 1], 'ro')
        # for ctr, xy in enumerate(zip(arr[:, 0].flat, arr[:, 1].flat)):
        #     xy = list(xy)
        #     xy[0] += 0.00001
        #     xy[1] += 0.00001
        #     ax.annotate(str(ctr), xy=xy)
        # plt.savefig('/home/benkoziol/htmp/pmesh/element images/element-69.png', dpi=300)
        plt.show()

